package Loops;

public class For_While_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=1;i<=5;i++)
		{
			System.out.println("repitition of i: "+i);
			int j=1;
			while(j<=5)
			{
				System.out.println("i="+i+":j="+j);
				j++;
			}
					
		}
	}

}
